n	font-size: 14px;\r\n}\r\n.t-panel-body{\r\n	padding:15px;\r\n}\r\n.t-red-bg{\r\n	background-color: #cd375e;\r\n	color: #fff;\r\n}\r\n.t-purple-bg{\r\n	background-color: #984475;\r\n	color: #fff;\r\n}\r\n.t-bice-bg{\r\n	background-color: #57678e;\r\n	color: #fff;\r\n}\r\n.t-list li {\r\n    width: 99%;\r\n    line-height: 28px;\r\n    word-wrap: normal;\r\n    text-overflow: ellipsis;\r\n    overflow: hidden;\r\n}\r\n.t-list li:last-child{\r\n	 border-bottom:none;\r\n}\r\n.t-list .thumbnail{\r\n    position: relative;\r\n    float: left;\r\n    width: 100px;\r\n    height: 75px;\r\n    margin: 8px 8px 8px 0;\r\n    overflow: hidden;\r\n    border: none;\r\n    background-color: #f6f6f6;\r\n}\r\n.t-list .hot-title {\r\n    height: 55px;\r\n    padding-top: 5px;\r\n    line-height: 25px;\r\n    overflow: hidden;\r\n    display: block;\r\n    font-size: 14px;\r\n}\r\n.t-list .views {\r\n    float: right;\r\n    color: #999;\r\n}\r\n.t-list .fa {\r\n    color: #999;\r\n}\r\n.t-list2{\r\n	font-size: 14px;\r\n}\r\n.t-list2 li{\r\n	line-height: 26px;\r\n}\r\n.img-bg{\r\n	width: 200px;\r\n	height: 150px;\r\n	background-color: #f6f6f6;\r\n}\r\n.text-gry-color{\r\n	color:#999;\r\n}\r\n.article{\r\n	padding:20px;\r\n	background-color: #fff;\r\n}\r\n.post{\r\n	position: relative;\r\n    background: #fff;\r\n    margin: 0 0 10px 0;\r\n    padding: 20px;\r\n}\r\n.entry-header h1 {\r\n    position: relative;\r\n    font-size: 16px;\r\n    font-size: 1.8rem;\r\n    line-height: 30px;\r\n    text-align: center;\r\n    margin: 35px -20px 20px -20px;\r\n    padding: 5px 20px;\r\n    border-left: 5px solid #e4393c;\r\n    border-right: 5px solid #e4393c;\r\n    background: #ebebeb;\r\n}\r\n.single-content {\r\n    font-size: 15px;\r\n    font-size: 1.5rem;\r\n    line-height: 1.8\r\n}\r\n.single-content a:link,.single-content a:visited{\r\n	color: #e4393c;\r\n}\r\n.single-content a:{\r\n	text-decoration: underline;\r\n}\r\n.single-meta {\r\n    position: absolute;\r\n    top: 15px;\r\n    right: 15px;\r\n}\r\n.single-meta li {\r\n    float: left;\r\n}\r\n.single-meta .views,.single-meta li a {\r\n    color: #999;\r\n    line-height: 26px;\r\n    margin: 0 5px 0 0;\r\n    padding: 0 8px;\r\n    display: block;\r\n    border: 1px solid #ddd;\r\n}\r\n\r\n.single-meta li a:hover {\r\n    background: #e4393c;\r\n    color: #fff;\r\n    border: 1px solid #e4393c\r\n}\r\n\r\n.single-meta a:hover {\r\n    color: #fff\r\n}\r\n#fontsize {\r\n    position: absolute;\r\n    top: 15px;\r\n    left: 20px;\r\n    line-height: 24px;\r\n    width: 240px;\r\n    padding: 0 0 1px 0;\r\n}\r\n.fontsmall {\r\n    font-size: 18px;\r\n    font-size: 1.8rem\r\n}\r\n.single-cat-tag {\r\n    position: absolute;\r\n    bottom: -1px;\r\n    left: 0;\r\n    width: 100%;\r\n    line-height: 28px;\r\n    height: 45px;\r\n    padding: 10px 0;\r\n    overflow: hidden;\r\n    border-radius: 0 0 2px 2px\r\n}\r\n.single-cat {\r\n    padding: 0 20px;\r\n}\r\n#social {\r\n    position: relative;\r\n    margin: 15px auto 80px;\r\n}\r\n.social-main {\r\n    position: relative;\r\n    margin: 0 auto;\r\n    width: 135px;\r\n}\r\n.social-main span {\r\n    float: left;\r\n}\r\n.social-main a {\r\n    color: #fff;\r\n    line-height: 35px;\r\n    text-align: center;\r\n    border-radius: 2px;\r\n}\r\n.like a {\r\n    background: #e4393c;\r\n    width: 135px;\r\n    display: block;\r\n    border: 0;\r\n}\r\n.ds-reward-stl {\r\n    text-align: left;\r\n    background: #fff;\r\n    padding: 0;\r\n    color: #666;\r\n    margin: 0;\r\n    width: 0;\r\n}\r\n#dsRewardBtn {\r\n    padding: 0;\r\n    margin: 0;\r\n    position: absolute;\r\n    background: #7ab951;\r\n    left: 110px;\r\n    top: -7px;\r\n    width: 50px;\r\n    height: 50px;\r\n    font-size: 16px;\r\n    font-weight: 600;\r\n    line-height: 43px;\r\n    display: block;\r\n    border: 4px solid #fff;\r\n    border-radius: 40px;\r\n    color: #FFF;\r\n}\r\n.share-s a {\r\n    background: #ecb842;\r\n    width: 135px;\r\n    display: block;\r\n    border: 0;\r\n}\r\n#share {\r\n    position: absolute;\r\n    top: -60px;\r\n    right: -18px;\r\n    width: 302px;\r\n    height: 68px;\r\n    display: none;\r\n    z-index: 999;\r\n}\r\n#respond {\r\n    background: #fff;\r\n    margin: 0 0 10px 0;\r\n    padding: 10px 20px;\r\n}\r\n.comment-reply-title{\r\n	font-size: 1.5rem;\r\n}\r\n.comments-title {\r\n    background: #fff;\r\n    margin-bottom: 0;\r\n    padding: 15px 20px;\r\n    font-size: 14px;\r\n}\r\n.comment-list li {\r\n    position: relative;\r\n    background: #fff;\r\n    margin: 0 0 10px 0;\r\n    padding: 15px 20px;\r\n    word-wrap: break-word;\r\n    word-break: break-all;\r\n}\r\n.comment-author {\r\n    color: #333;\r\n    line-height: 23px;\r\n}\r\n.comment-meta{\r\n	color:#999;\r\n}\r\n.vcard .avatar {\r\n    float: left;\r\n    width: 64px;\r\n    height: 64px;\r\n    margin: 7px 10px 0 0;\r\n    padding: 3px;\r\n    border-radius: 5px;\r\n    border: 1px solid #ddd;\r\n    transition: all .8s ease 0s;\r\n}\r\n.comment-list .avatar {\r\n    border: 1px solid #ccc;\r\n}\r\nb{\r\n	border: 0;\r\n    font-family: inherit;\r\n    font-size: 100%;\r\n    font-style: inherit;\r\n    margin: 0;\r\n    outline: 0;\r\n    padding: 0;\r\n    vertical-align: baseline;\r\n}\r\n/*footer*/\r\n.footer{\r\n    text-align: center;\r\n    width: 100%;\r\n    bottom: 0;\r\n    margin-top: 20px;\r\n    position:fixed;\r\n    padding: 10px 0;\r\n    background-color: #fff;\r\n}\r\n.article-close {\r\n    padding: 10px 0 0;\r\n    text-align: center;\r\n}\r\n.scroll-top.in{\r\n	background-color: #c40000;\r\n}\r\n.pace .pace-progress, .nano &gt; .nano-pane &gt; .nano-slider{\r\n	background-color: #c40000;\r\n}</pre><p>&nbsp;</p>', '', '1', 'Tapestry-resteasy组件的使用', null, null);

